import React from "react";
import Fab from '@material-ui/core/Fab';
import Zoom from '@material-ui/core/Zoom';
import AddBoxIcon from '@material-ui/icons/AddBox';
function CreateArea(props) {
  return (
    <div>
      <form>
    {props.expandState?  <input
        autoComplete="off"
        onChange={props.onChange} name="title" placeholder="Title"
        value={props.titleValue}
      />:null}
        <textarea
          onClick={props.onContentClick}
          onChange={props.onChange}
          value={props.contentValue}
          name="content" placeholder="Take a note..." rows={props.expandState?3:1} />

        <Zoom in={props.expandState?true:false}>
          <Fab
            onClick={props.onAddClick}>
            <AddBoxIcon />
          </Fab>
        </Zoom>



      </form>
    </div>
  );
}

export default CreateArea;
